import React, { Component } from 'react';
// import logo from '../logo.svg';
import { connect } from 'react-redux';
import './App.css';
import Add_modal from '../components/Add_modal';
import Edit_modal from '../components/Edit_modal';
import Detail_modal from '../components/Detail_modal';
import Delete_modal from '../components/Delete_modal';
import Car_table from '../components/Car_table';
// import  * as actionCreators  from '../actions/mainActions';
import  loadCars  from '../actions/mainActions';

class App extends Component {

  constructor(props){
      super(props);
      this.state = {
        items:[],
        isLoaded:'false',
      }
  }

  componentDidMount(){

          fetch("http://localhost:60665/api/values")
          .then((res) => {
            this.state.isLoaded = "sss";
            this.setState({
              isLoaded : 'true',
              items:res.json(),
            })
            // return res.json();
          })
          // .then(json=>{
          //   this.setState({
          //     isLoaded : 'true',
          //     items:json,
          //   })
          // })      
  }

  render() {
    return (
      <div className="App container">
            <div class="row" id="add_btn_div">
              <button id="add_btn" type="button" class="btn btn-primary" data-toggle="modal" data-target="#addModal">
                    Add New
              </button>
            </div>
            {this.state.isLoaded}
            {this.state.items}
            <Car_table />
            <Add_modal />
            <Edit_modal />
            <Detail_modal />
            <Delete_modal />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return state
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadCars: ()=>{
      dispatch(loadCars);
    }
  }
}

// export default App;
// export default connect(mapStateToProps, actionCreators)(App);
export default connect(mapStateToProps, mapDispatchToProps)(App);
