import React from 'react';
// with es6
// import {Button, Glyphicon} from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
// import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
import Control_icon from './Control_icon';
import FlagIcon from 'react-flag-kit/lib/FlagIcon';
 
// export default () => <FlagIcon code="WW" size={48} />
// // with es5
// var ReactBsTable = require('react-bootstrap-table');
// var BootstrapTable = ReactBsTable.BootstrapTable;
// var TableHeaderColumn = ReactBsTable.TableHeaderColumn;
// require('../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css');
       
var products = [
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr:  Control_icon
    },
    {
        manufacturer:'Chevrolet',
        make:'Corvette',
        model:'Z06',
        year:'2014',
        ctr: <FlagIcon data-toggle="modal" data-target="#editModal" code="WW" size={35} />
                
    },
    {
        manufacturer:'Dodge',
        make:'Challenger',
        model:'Hellcot',
        year:'2014',
        ctr: 'icon_tag'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr: 'icon_tag'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr: 'icon_tag'
    }
];

const columns = [
    {
    dataField: 'manufacturer',
    text: 'Manufacturer',
    sort: true
    },
    {
    dataField: 'make',
    text: 'Make',
    sort: true
    }, 
    {
    dataField: 'model',
    text: 'Model',
    sort: true
    }, 
    {
    dataField: 'year',
    text: 'Year',
    sort: true
    }, 
    {
    dataField: 'ctr',
    text: 'Action',
    sort: true
    }
];
  
  const defaultSorted = [{
    dataField: 'manufacturer',
    order: 'desc'
  }];

class Car_table extends React.Component {
    
    render() {
      return (
        <div>
          <BootstrapTable
            keyField="manufacturer"
            data={ products }
            columns={ columns }
            defaultSorted={ defaultSorted } 
            />
        </div>
      );
    }
  }

  export default Car_table;
