/**
 * You will want to include this bit of css
 *
 * .modal-container {
 *   position: relative;
 * }
 * .modal-container .modal, .modal-container .modal-backdrop {
 *   position: absolute;
 * }
 */

import React from 'react';
import { connect } from 'react-redux'
// import { Modal, Button, Label, FormControl} from 'react-bootstrap';

class Edit_modal extends React.Component {
    constructor(props, context) {
      super(props, context);
      this.handleHide = this.handleHide.bind(this);
      this.state = {
        show: false,
        inputManufacturer_val:'',
        inputMake_val:'',
        inputModel_val:'',
        inputYear_val: '',
      };
      this.handleChange = this.handleChange.bind(this);
    //   console.log(this.props.clicked_row);
    }
    componentDidMount() {
        // this.setState({inputValue: this.props.inputValue});
        console.log('------------didmount-------------------')
        console.log(this.props.clicked_row);
        // this.setState({
        //     inputManufacturer_val:this.props.clicked_row.manufacturer,
        //     inputMake_val:this.props.clicked_row.make,
        //     inputModel_val:this.props.clicked_row.model,
        //     inputYear_val:this.props.clicked_row.year,
        //   });
     }
     componentWillReceiveProps() {
         console.log('---------------------will recceive------------------');
         console.log(this.props);
        this.setState({
            inputManufacturer_val:this.props.clicked_row.manufacturer,
            inputMake_val:this.props.clicked_row.make,
            inputModel_val:this.props.clicked_row.model,
            inputYear_val:this.props.clicked_row.year,
        });
     }
    handleChange(event) {
        // console.log(event);
        this.setState({inputManufacturer_val: event.target.value});
    }
    edit_items(e) {
        var items = this.props.loaded_items;
        items.forEach(item => {
            // console.log(item);
            // if(item.id == this.props.clicked_row.id){
            //     item.manufacturer = this.refs.inputManufacturer.value;
            //     item.make = this.refs.inputMake.value;
            //     item.model = this.refs.inputModel.value;
            //     item.year = this.refs.inputYear.value;
            // }
        });
    }
    handleHide() {
      this.setState({ show: false });
    }
    render() {
        console.log('----------------modal----------------------');
        console.log(this.props);
      return (
        <div className="modal-container" style={{ height: 200 }}>
            <div class="modal fade" id="editModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLabel">Edit Car</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <div class="form-group row">
                        <label for="manufacturer" class="col-sm-4 col-form-label" style={{'text-align': 'left'}}>Manufacturer</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control" ref="inputManufacturer" id="inputManufacturer" value={this.state.inputManufacturer_val}  onChange={this.handleChange}></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputMake" class="col-sm-4 col-form-label" style={{'text-align': 'left'}}>Make</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control"  ref="inputMake" id="inputMake" value={this.props.clicked_row.make}></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputModel" class="col-sm-4 col-form-label" style={{'text-align': 'left'}}>Model</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control"  ref="inputModel" id="inputModel" value={this.props.clicked_row.model}></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputYear" class="col-sm-4 col-form-label" style={{'text-align': 'left'}}>Year</label>
                        <div class="col-sm-8">
                        <input type="number" class="form-control"  ref="inputYear" id="inputYear" min="1" value={this.props.clicked_row.year}></input>
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-primary" onClick={this.edit_items.bind(this)}>Save</button>
                    <button type="button" class="btn btn-secondary"  ref="cancel_btn" data-dismiss="modal">Cancel</button>
                </div>
                </div>
            </div>
            </div>
        </div>
      );
    }
  }

  const mapStateToProps = (state) => {
    return {
      todos: state.main
    }
  }

  
  
  const mapDispatchToProps = (dispatch) => {
    return {
      onTodoClick: id => {
        dispatch({
          type: 'SELECT_ROW',
          row: id
        })
      }
    }
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Edit_modal);

//   render(<My_modal />);

