let defaultState = {
    car_datas : [
        {
            id:1,
            manufacturer:'ex:Ford',
            make:'ex:Mustang',
            model:'ex:GT',
            year:2013,
        }
    ]
}

const mainReducer = (state = defaultState ,action) =>{
    switch(action.type){
        case "LOAD_CARS": 
            state = {
                ...state,
                car_datas: action.car_datas,
            };
            break;
            
    }
    return state;
};
export default mainReducer;
