import React from 'react';
// with es6
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
// // with es5
// var ReactBsTable = require('react-bootstrap-table');
// var BootstrapTable = ReactBsTable.BootstrapTable;
// var TableHeaderColumn = ReactBsTable.TableHeaderColumn;
// require('../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css');

var icon_tag = '<i class="fa fa-pencil-alt" data-toggle="modal" data-target="#editModal"></i><i class="fa fa-trash-alt" data-toggle="modal" data-target="#deleteModal"></i>'

var products = [
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr:'<i class="fa fa-pencil-alt" data-toggle="modal" data-target="#editModal"></i>'
    },
    {
        manufacturer:'Chevrolet',
        make:'Corvette',
        model:'Z06',
        year:'2014'
    },
    {
        manufacturer:'Dodge',
        make:'Challenger',
        model:'Hellcot',
        year:'2014'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016'
    }
];

class Car_table extends React.Component {
    constructor(props) {
      super(props);
      this.options = {
        defaultSortName: 'manufacturer',  // default sort column name
        defaultSortOrder: 'desc'  // default sort order
      };
    }
    
    render() {
      return (
        <div>
          <BootstrapTable data={ products } options={ this.options } hover  version='4'>
            <TableHeaderColumn dataField='manufacturer' isKey dataSort>Manufacturer</TableHeaderColumn>
            <TableHeaderColumn dataField='make' dataSort>Make</TableHeaderColumn>
            <TableHeaderColumn dataField='model' dataSort>Model</TableHeaderColumn>
            <TableHeaderColumn dataField='year' >Year</TableHeaderColumn>
            <TableHeaderColumn dataField='year'> </TableHeaderColumn>
          </BootstrapTable>
        </div>
      );
    }
  }

  export default Car_table;
