import axios from 'axios';

export function load_cars(){
    var data=[{}];
    var isLoaded = false;
    axios.get("http://localhost:60665/api/values")
    .then((response)=>{
      data = response.data;
      isLoaded = true;
    })
    return{
      type: "LOAD_CARS",
      data: data,
      isLoaded: isLoaded
    }
}

export function show_car(){
    return{
      type: "SHOW_CAR",
      data: data,
      isLoaded: isLoaded
    }
}

