import axios from 'axios';

export function loadCars(){
    return(dispatch)=>{
        return axios.get("http://localhost:60665/api/values").then((response)=>{
            return {
                type:"LOAD_CARS",
                car_datas:response.car_datas
            }
        })
    }
}

