import React, { Component } from 'react';
import axios from 'axios';
// import logo from '../logo.svg';
import { connect } from 'react-redux';
import './App.css';
import Add_modal from '../components/Add_modal';
import Edit_modal from '../components/Edit_modal';
import Detail_modal from '../components/Detail_modal';
import Delete_modal from '../components/Delete_modal';
import Car_table from '../components/Car_table';
import  { load_cars }  from '../actions/mainActions';

class App extends Component {

  constructor(props){
      super(props);
      this.state = {
        items:[],
        isLoaded:false,
      }
      // console.log(this.props.main);
  }

  componentWillMount(){
          axios.get("http://localhost:60665/api/values")
          .then((response)=>{
            this.setState({
              isLoaded : true,
              items:response.data,
              clicked_item: {}
            })
          })
  }
  test(){
    alert();
  }
  render() {
    if(!this.state.isLoaded){
      return (<div>Loading...</div>);
    }else{
      return (
        <div className="App container">
              <div class="row" id="add_btn_div">
                <button onClick={this.test} type="button" class="btn btn-primary">
                      test button
                </button>
                <button id="add_btn" type="button" class="btn btn-primary" data-toggle="modal" data-target="#addModal">
                      Add New
                </button>
              </div>
              <Car_table datas={this.state.items} show_car={() => this.props.show_car()}/>
              <Add_modal />
              <Edit_modal />
              <Detail_modal />
              <Delete_modal />
        </div>
      );
    }
  }
}

const mapStateToProps = (state) => {
  return {
    main: state.main,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
     load_cars: () => {
        dispatch(load_cars());
     },
     show_car: () => {
      dispatch(show_car());
     }
  }
}

// export default App;
// export default connect(mapStateToProps, actionCreators)(App);
export default connect(mapStateToProps, mapDispatchToProps)(App);
