/**
 * You will want to include this bit of css
 *
 * .modal-container {
 *   position: relative;
 * }
 * .modal-container .modal, .modal-container .modal-backdrop {
 *   position: absolute;
 * }
 */
import React from 'react';
import { Modal, Button, Label, FormControl} from 'react-bootstrap';

class My_modal extends React.Component {
    constructor(props, context) {
      super(props, context);
  
      this.handleHide = this.handleHide.bind(this);
  
      this.state = {
        show: false
      };
    }
  
    handleHide() {
      this.setState({ show: false });
    }
    render() {
      return (
        <div className="modal-container" style={{ height: 200 }}>
          <Button
            bsStyle="primary"
            bsSize="middle"
            onClick={() => this.setState({ show: true })}
          >
            Add New
          </Button>
  
          <Modal
            show={this.state.show}
            onHide={this.handleHide}
            container={this}
            aria-labelledby="contained-modal-title"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title">
                Contained Modal
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div class="form-group">
            <Label bsStyle="default">Default</Label>
                
            </div>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={this.handleHide} bsStyle="primary">Save</Button>
              <Button onClick={this.handleHide} >Cancel</Button>
               
            </Modal.Footer>
          </Modal>
        </div>
      );
    }
  }
  
//   render(<My_modal />);

  export default My_modal;
