let defaultState = {
     isLoaded: false,
     data: [{}],
     clicked_row: null,
}

const mainReducer = (state = defaultState ,action) =>{
    switch(action.type){
        case "SELECT_ROW": 
            console.log()
            return {...state, clicked_row: action.row}
            break;
    }
    return state;
};

export default mainReducer;
