let defaultState = {
     isLoaded: false,
     data: [{}],
     clicked_row: null,
     clicked_
}

const mainReducer = (state = defaultState ,action) =>{
    switch(action.type){
        case "LOAD_CARS": 
            state = {
                ...state,
                data: action.data,
                isLoaded: action.isLoaded
            };
            break;
        case "SHOW_CAR": 
            state = {
                ...state,
                clicked_row: action.clicked_row
            };
            break;
    }
    return state;
};

export default mainReducer;
