/**
 * You will want to include this bit of css
 *
 * .modal-container {
 *   position: relative;
 * }
 * .modal-container .modal, .modal-container .modal-backdrop {
 *   position: absolute;
 * }
 */

import React from 'react';
// import { Modal, Button, Label, FormControl} from 'react-bootstrap';

class My_modal extends React.Component {
    constructor(props, context) {
      super(props, context);
  
      this.handleHide = this.handleHide.bind(this);
  
      this.state = {
        show: false
      };
    }
  
    handleHide() {
      this.setState({ show: false });
    }
    render() {
      return (
        <div className="modal-container" style={{ height: 200 }}>
         
            <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
            Launch demo modal
            </button>
            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div class="modal-body">
                    <div class="form-group row">
                        <label for="manufacturer" class="col-sm-4 col-form-label">Manufacturer</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control" id="inputManufacturer" placeholder="Ford"></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputMake" class="col-sm-4 col-form-label">Make</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control" id="inputMake" placeholder="Musatang"></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputModel" class="col-sm-4 col-form-label">Model</label>
                        <div class="col-sm-8">
                        <input type="text" class="form-control" id="inputModel" placeholder="GT"></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="inputYear" class="col-sm-4 col-form-label">Year</label>
                        <div class="col-sm-8">
                        <input type="number" class="form-control" id="inputYear" placeholder="2015"></input>
                        </div>
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                    <button type="button" class="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
            </div>
        </div>
      );
    }
  }
  
//   render(<My_modal />);

  export default My_modal;
