import { createStore, combineReducers, applyMiddleware } from "redux";
import logger from "redux-logger";

import mainReducer from "./reducers/mainReducer";

export default createStore(
    combineReducers({
        mainReducer,
    }),
    {},
    applyMiddleware(logger())
);
