import React from 'react';
// with es6
// import {Button, Glyphicon} from 'react-bootstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
// // with es5
// var ReactBsTable = require('react-bootstrap-table');
// var BootstrapTable = ReactBsTable.BootstrapTable;
// var TableHeaderColumn = ReactBsTable.TableHeaderColumn;
// require('../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css');
       
var products = [
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        
    },
    {
        manufacturer:'Chevrolet',
        make:'Corvette',
        model:'Z06',
        year:'2014',
       
    },
    {
        manufacturer:'Dodge',
        make:'Challenger',
        model:'Hellcot',
        year:'2014',
     
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
         
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
       
    }
];

class Car_table extends React.Component {
    constructor(props) {
      super(props);
      this.options = {
        defaultSortName: 'manufacturer',  // default sort column name
        defaultSortOrder: 'desc'  // default sort order
      };
    }

    dataAction(cell, row) {   // String example
        return `<center><i class="fa fa-pencil" style="font-size:20px;" data-toggle="modal" data-target="#editModal">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</i><i class="fa fa-trash" style="font-size:20px;" data-toggle="modal" data-target="#deleteModal"></i></center>`;
    }
    toAtag(cell, row) {
        return `<a href="" data-toggle="modal" data-target="#detailModal">${cell}</a>`;
    }
    
    render() {
      return (
        <div class="row" id="cartable_div">
          <BootstrapTable data={ products } options={ this.options } hover  version='4'>
            <TableHeaderColumn dataField='manufacturer' isKey dataSort dataFormat={ this.toAtag }>Manufacturer</TableHeaderColumn>
            <TableHeaderColumn dataField='make' dataSort>Make</TableHeaderColumn>
            <TableHeaderColumn dataField='model' dataSort>Model</TableHeaderColumn>
            <TableHeaderColumn dataField='year' dataSort width='90'>Year</TableHeaderColumn>
            <TableHeaderColumn  dataFormat={ this.dataAction } width='120'> </TableHeaderColumn>
          </BootstrapTable>
        </div>
      );
    }
  }

  export default Car_table;
