import axios from 'axios';

export function load_Cars(){
    var data;
    axios.get("http://localhost:60665/api/values")
    .then((response)=>{
      data = response.data;
      isLoaded = true;
      this.setState({
        isLoaded : true,
        items:response.data,
      })
    })
}
