import React from 'react';
// with es6
// import {Button, Glyphicon} from 'react-bootstrap';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table-next';
// import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
import Control_icon from './Control_icon';
import FlagIcon from 'react-flag-kit/lib/FlagIcon';
 
// export default () => <FlagIcon code="WW" size={48} />
// // with es5
// var ReactBsTable = require('react-bootstrap-table');
// var BootstrapTable = ReactBsTable.BootstrapTable;
// var TableHeaderColumn = ReactBsTable.TableHeaderColumn;
// require('../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css');
       
var products = [
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr:  Control_icon
    },
    {
        manufacturer:'Chevrolet',
        make:'Corvette',
        model:'Z06',
        year:'2014',
        ctr: <FlagIcon code="WW" size={48} />
    },
    {
        manufacturer:'Dodge',
        make:'Challenger',
        model:'Hellcot',
        year:'2014',
        ctr: 'icon_tag'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr: 'icon_tag'
    },
    {
        manufacturer:'Ford',
        make:'Mustang',
        model:'GT',
        year:'2016',
        ctr: 'icon_tag'
    }
];

const columns = [{
    dataField: 'id',
    text: 'Product ID',
    sort: true
  }, {
    dataField: 'name',
    text: 'Product Name',
    sort: true
  }, {
    dataField: 'price',
    text: 'Product Price',
    sort: true
  }];
  
  const defaultSorted = [{
    dataField: 'name',
    order: 'desc'
  }];

class Car_table extends React.Component {
    constructor(props) {
      super(props);
      this.options = {
        defaultSortName: 'manufacturer',  // default sort column name
        defaultSortOrder: 'desc'  // default sort order
      };
    }
    
    render() {
      return (
        <div>
          <BootstrapTable
            keyField="id"
            data={ products }
            columns={ columns }
            defaultSorted={ defaultSorted } 
            />
        </div>
      );
    }
  }

  export default Car_table;
