import React from 'react';
// with es6
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';
// // with es5
// var ReactBsTable = require('react-bootstrap-table');
// var BootstrapTable = ReactBsTable.BootstrapTable;
// var TableHeaderColumn = ReactBsTable.TableHeaderColumn;

class Car_table extends React.Component {
    constructor(props) {
      super(props);
  
      this.options = {
        defaultSortName: 'name',  // default sort column name
        defaultSortOrder: 'desc'  // default sort order
      };
      this.products = [
        {
            'manufacturer':'Ford',
            'make':'Mustang',
            'model':'GT',
            'year':'2016',
        },
        {
            'manufacturer':'Chevrolet',
            'make':'Corvette',
            'model':'Z06',
            'year':'2014',
        },
        {
            'manufacturer':'Dodge',
            'make':'Challenger',
            'model':'Hellcot',
            'year':'2014',
        },
        {
            'manufacturer':'Ford',
            'make':'Mustang',
            'model':'GT',
            'year':'2016',
        },
        {
            'manufacturer':'Ford',
            'make':'Mustang',
            'model':'GT',
            'year':'2016',
        },
    ];
    }
    
    render() {
      return (
        <div>
          <BootstrapTable data={ this.products } options={ this.options }>
            <TableHeaderColumn dataField='id' isKey dataSort>Manufacturer</TableHeaderColumn>
            <TableHeaderColumn dataField='name' dataSort>Make</TableHeaderColumn>
            <TableHeaderColumn dataField='price'>Model</TableHeaderColumn>
            <TableHeaderColumn dataField='price'>Year</TableHeaderColumn>
            <TableHeaderColumn dataField='price'> </TableHeaderColumn>
          </BootstrapTable>
        </div>
      );
    }
  }

  export default Car_table;
